const Builtin = require('./builtin');

const aliases = {};

exports.alias = class Alias extends Builtin
{
  constructor (name, args, opts, state)
  {
    super(name, args, opts, state);
    let exitCode = 0;

    if (args.length == 0) {
      for (let name in aliases) {
        this._stdout.write(`${name}='${aliases[name]}'\n`);
      }
    }
    else {
      for (let arg of args) {
        let [name, ...value] = arg.split('=');
        value = value.join('=');

        if (value.length == 0) {
          value = exports.alias.getAlias(name);
          if (value) {
            this._stdout.write(`${name}='${value}'\n`);
          }
          else {
            exitCode = 1;
          }
        }
        else {
          exports.alias.createAlias(name, value);
        }
      }
    }
    process.nextTick(() => this.emit('exit', exitCode));
  }

  static createAlias (name, value)
  {
    aliases[name] = value;
  }

  static getAlias (name)
  {
    return aliases[name];
  }

  static removeAlias (name)
  {
    if (!(name in aliases)) {
      return false;
    }
    delete aliases[name];
    return true;
  }
}


exports.unalias = class Unalias extends Builtin
{
  constructor (name, args, opts)
  {
    super(name, args, opts);
    let exitCode = 0;

    if (args.length == 0) {
      this._stderr.write(`unalias: not enough arguments\n`);
      exitCode = 1;
    }
    else {
      for (let arg of args) {
        if (!exports.alias.removeAlias(arg)) {
          this._stderr.write(`unalias: no such alias ${arg}\n`);
          exitCode = 1;
        }
      }
    }
    process.nextTick(() => this.emit('exit', exitCode));
  }
}

const EventEmitter = require('events');
const {PassThrough} = require('stream');

module.exports = class Builtin extends EventEmitter
{
  constructor (name, args, opts, state)
  {
    super();
    Object.assign(this, {
      cwd: process.cwd(),
      env: process.env,
      stdio: ['pipe','pipe','pipe']
    }, opts);

    this.stdio[0] = this.stdin = this.stdio[0] == 'pipe' ?
      new PassThrough() : null;

    this.stdio[1] = this.stdout = this.stdio[1] == 'pipe' ?
      new PassThrough() : null;

    this.stdio[2] = this.stderr = this.stdio[2] == 'pipe' ?
      new PassThrough() : null;

    this._stdin = this.stdin || process.stdin;
    this._stdout = this.stdout || process.stdout;
    this._stderr = this.stderr || process.stderr;

    this.on('exit', () => {
      this.stdin && this.stdin.end();
      this.stdout && this.stdout.end();
      this.stderr && this.stderr.end();
    });
  }
}

