module.exports = async function exec (cmd, state=new State, ctx={})
{
  const pipe_stdout = new PassThrough;
  let out = '';

  return new Promise(async (res, rej) => {
    pipe_stdout
    .on('error', (err) => {
      rej(err);
    })
    .on('data', (data) => {
      out += data.toString();
    })
