    if (name in builtins) {
      proc = new builtins[name](name, args, {stdio, argv0: origName}, state);
    }
    else {
      let cmdpath;

      cmdpath = locateCommand(name, node.dot);

      proc = spawn(cmdpath, args, {stdio, argv0: origName, env: state.env});
    }

    if (ctx.pipe_stdin) {
      if (ctx.pipe_stdin.readable) {
        ctx.pipe_stdin
        .on('data', (data) => {
          if (proc.stdin.writable) {
            proc.stdin.write(data);
          }
        })
        .on('end', () => {
          proc.stdin.end();
        })
        .on('error', (err) => {
          proc.stdin.end();
          rej(err);
        })

        proc.stdin
        .on('error', (err) => {
          rej(err);
        })
      }
      else {
        proc.stdin.end();
      }
    }

    for (let io in redirs) {
      proc.stdio[io]
      .on('data', (data) => {
        for (let stream of redirs[io]) {
          if (!isNaN(stream)) {
            ctx.stdio[stream].write(data);
          };
          if (stream && stream.writable) {
            stream.write(data);
          }
        }
      })
      .on('end', () => {
        for (let stream of redirs[io]) {
          if (!isNaN(stream)) continue;
          if (stream && stream.writable) {
            stream.end();
          }
        }
      })
      .on('error', (err) => {
        for (let stream of redirs[io]) {
          if (stream && isNaN(stream)) continue;
          proc.stdio[stream].end();
        }
      })
    }

    if (ctx.pipe_stdout) {
      proc.stdout
      .on('data', (data) => {
        ctx.pipe_stdout.write(data);
      })
      .on('end', () => {
        ctx.pipe_stdout.end();
      })
      .on('error', (err) => {
        ctx.pipe_stdout.end();
        rej(err);
      })
    }

    proc
    .on('exit', (code, signal) => {
      log('exit "%s"', origName);
      if (code || signal) return rej(code || signal);
      res();
    })
    .on('error', (err) => {
      rej(err);
    })
  });
  state.restore();
}

